import { Component, OnInit } from '@angular/core';
import { Router, RouterModule } from '@angular/router';
import { HomeComponent } from '../home/home.component';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-dsgvo',
  templateUrl: './dsgvo.component.html'
})
export class DsgvoComponent implements OnInit {
  constructor(private router: Router, private http: HttpClient) { }

  //Do schick ma die Cookie-Infos an des Backend
  acceptPrivacyPolicy() {
    this.router.navigate(["home"]);


  }
  ngOnInit() { }



  sendDataToBackend(data: any) {
    const url = 'https://example.com/api/data'; // Ersetzen Sie dies durch die URL Ihres Backends

    return this.http.post(url, data);

  }
}

<!DOCTYPE html>
<html lang="de">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">

  <!-- Füge Bootstrap-CSS hinzu (CDN-Link) -->
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
</head>
<body>
  
    <div class="container mt-5">
      <div class="row">
        <div class="col-md-6 offset-md-3">
          <div class="card">
            <div class="card-header">
              <h1 class="text-center">Datenschutzerklärung</h1>
            </div>
            <div class="card-body">
              <p>
                Um diese App nutzen zu können, ist es notwendig, Personendaten sowie Routen, die Sie in dieser App wählen, vorübergehend zu speichern. Dafür ist eine Zustimmung im Sinne der DSGVO notwendig.
              </p>
              <a href="https://www.dsb.gv.at/recht-entscheidungen/gesetze-in-oesterreich.html" > Allgemeine Bestimmungen</a>
              <br />
              <div class="form-check">
                <input class="form-check-input" type="checkbox" id="privacyPolicyCheckbox">
                <label class="form-check-label">Ich stimme der Datenschutzerklärung zu</label>

              </div>
                <button class="btn-primary" (click) ="acceptPrivacyPolicy()">Absenden</button>
            </div>

          </div>
        </div>
      </div>
    </div>
  
    
              
      

  <!-- Füge Bootstrap-JavaScript hinzu (CDN-Link) -->
  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.min.js"></script>
</body>
</html>
