import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router, RouterModule } from '@angular/router';
import { DataService } from '../data.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent
{

  constructor(private router: Router, private http: HttpClient, private dataService: DataService) { }



  async redirectToAdminWindow()
  {
    var { value: password } = await Swal.fire({
      title: "Enter your password",
      input: "password",
      inputLabel: "Password",
      inputPlaceholder: "Enter your password",
      inputAttributes: {
        maxlength: "10",
        autocapitalize: "off",
        autocorrect: "off"
      }
    });
    if (password == "EVG1") {
      this.router.navigate(["adminwindow"]);
    }
    else
    {
      Swal.fire({
        icon: "error",
        title: "Falsches Passwort",
        text: "",
 
      });
    }
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Your App Title</title>
  <style>
    

    #btn_adminwindow {
      margin-top: 10px; /* Optional: Just to add some space above the button */
      display: flex;
      justify-content: center; /* Horizontal zentrieren */
      align-items: center; /* Vertikal zentrieren */
      padding: 10px; /* Optional: Adjust padding as needed */
      background-color: #007bff; /* Optional: Add a background color */
      color: #fff; /* Optional: Set text color */
    }
  </style>
</head>
<body>
  <!-- Your existing HTML content -->
  <app-nav-menu></app-nav-menu>
  <p id="paragraph-user">made by Kevin Eppensteiner</p>
  <button id="btn_adminwindow" (click)="redirectToAdminWindow()">Admin-login</button>
</body>
</html>
