import { Component } from '@angular/core';
import { Router, RouterModule } from '@angular/router';



@Component({
  selector: 'app-roomidmanual',
  templateUrl: './roomidmanual.component.html'
})
export class RoomIdManualComponent {

   handleFormSubmit(): void {
  // Hole den Inhalt des Input-Feldes
  const zielraumInput = document.getElementById('zielraumInput') as HTMLInputElement;
     const zielraumInhalt: string = zielraumInput.value;
     console.log(zielraumInhalt);
 }
}
<app-nav-menu></app-nav-menu>
<h1>
  manual
</h1>
<form (submit)="handleSubmit()">
  <label> Bitte den gewünschten Zielraum eingeben!</label>
  <input type="text" />
  <button type="submit">Submit</button>

</form>
<style>
  form{
    align-content:center;
    align-items:center;
  }
</style>
